def relative_to_assets(path: str) -> Path:
    return ASSETS_PATH / Path(path)

def open_file_with_delay(filename,username, delay=2000):
    script_path = OUTPUT_PATH / filename
    subprocess.Popen(['python', str(script_path), username])
    window.after(delay, window.destroy)

def open_del(filename,username, delay=8000):
    script_path = OUTPUT_PATH / filename
    subprocess.Popen(['python', str(script_path), username])
    window.after(delay, window.destroy)

def meal_type_changed(meal_type):
    
    fetch_nutrition_plan(meal_type)


def fetch_nutrition_plan(meal_type):
    
    conn = mysql.connector.connect(**DB_CONFIG)
    
    cursor = conn.cursor()
    
   
   
    cursor.execute("SELECT MEALTIME,MEALTYPE,CALORIECOUNT,DESCRIPTION FROM NUTRITIONPLANS WHERE MEALTYPE=%s", (meal_type,))
    
    
    results = cursor.fetchall()
    
    
    result_str = "-" * 135 + "\n"
    result_str += "MEALTIME  | MEALTYPE\t| CALORIE COUNT\t| DESCRIPTION\n"
    result_str += "-" * 135 + "\n"
    
    for row in results:
        
        if row[0]=="Breakfast":
            if row[1]=="Low Carb" or  row[1]=="High Protein" or  row[1]=="Weight Loss":
                result_str += f"{row[0]}    | {row[1]}\t| {row[2]}\t\t| {row[3]}\n"
            else:
                result_str += f"{row[0]}    | {row[1]}\t\t| {row[2]}\t\t| {row[3]}\n"
        else:
            if row[1]=="Low Carb" or  row[1]=="High Protein" or  row[1]=="Weight Loss":
                result_str += f"{row[0]}\t   | {row[1]}\t| {row[2]}\t\t| {row[3]}\n"
            else:
                result_str += f"{row[0]}\t   | {row[1]}\t\t| {row[2]}\t\t| {row[3]}\n"
    result_str += "-" * 135 + "\n"
    canvas.delete("result_label")  
    
    
    canvas.create_text(
        470, 535, 
        text=result_str, 
        font=("Otomanopee One", 11), 
        fill="#84A928", 
        anchor="nw", 
        tags="result_label"  
    )
    
    # Close the connection
    cursor.close()
    conn.close()

